import * as d3 from 'd3';

export function drawAverageAreaChart(height, width){

// set the dimensions and margins of the graph
var margin = {top: 30, right: 30, bottom: 70, left: 60},
    width = 460 - margin.left - margin.right,
    height = 400 - margin.top - margin.bottom;

// append the svg object to the body of the page
var svg = d3.select("#averagearea")
  .append("svg")
    .attr("width", width + margin.left + margin.right)
    .attr("height", height + margin.top + margin.bottom)
  .append("g")
    .attr("transform",
          "translate(" + margin.left + "," + margin.top + ")");

// Parse the Data

var myData  = d3.csv("https://raw.githubusercontent.com/carlincherry/dsci554projectdata/main/TumorArea.csv");

myData.then(function(data) {

var tooltip = d3.select("averagearea").append("div").attr("class", "toolTip")

// X axis
var x = d3.scaleBand()
  .range([ 0, width ])
  .domain(data.map(function(d) { return d.stage; }))
  .padding(0.2);
svg.append("g")
  .attr("transform", "translate(0," + height + ")")
  .call(d3.axisBottom(x))
  .selectAll("text")
    .attr("transform", "translate(-10,0)rotate(-45)")
    .style("text-anchor", "end");

// Add Y axis
var y = d3.scaleLinear()
  .domain([0, 2000])
  .range([ height, 0]);
svg.append("g")
  .call(d3.axisLeft(y));

// Bars
svg.selectAll("mybar")
  .data(data)
  .enter()
  .append("rect")
    .attr("x", function(d) { return x(d.stage); })
    .attr("y", function(d) { return y(d.area); })
    .attr("width", x.bandwidth())
    .attr("height", function(d) { return height - y(d.area); })
    .attr("fill", "#28a745")
    .on("mouseover", function () {
      d3.select(this).style("fill", '#005A32').style('fill-opacity',1)})
      .on("mousemove", function(d){
        tooltip
          .style("left", d3.event.pageX - 50 + "px")
          .style("top", d3.event.pageY - 70 + "px")
          .style("display", "inline-block")
          .html((d.stage) + "<br>" + "£" + (d.area));
    })
    .on("mouseout", function(d){ tooltip.style("display", "none");})
      .on("mouseout", function () {
      d3.select(this).style("fill", '#28a745').style('fill-opacity',1)});
  })



}
