import React from "react";
// reactstrap components
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Row,
  Col,
} from "reactstrap";
// core components

function About() {
  return (
    <>
      <div className="content">

<Row>
          <Col md="12">
            <Card>
              <CardHeader>
                <CardTitle tag="h4">Dashboard Purpose and Instructions for Use</CardTitle>
              </CardHeader>
              <CardBody tag="h7">
                <p className="text-muted">City of Hope is a specialized hospital and research center in the greater Los Angeles area, serving many patients from far outside the local region. The hospital employs clinicians and researchers with expertise in many different types of cancers (and other diseases), and patients come to the hospital at various clinical stages; from diagnosis to palliative care. </p>
                <p className="text-muted">We use anonymized cancer patient data from Kaggle to create a dashboard that City of Hope can use in combination with their own data to aid in overseeing the flow of tens of thousands of patients that come in each year. The dashboard greatly enhances other information City of Hope collects, which in turn helps guide clinical management of cancer patients. </p>
                <p className="blockquote blockquote-primary">
                  This dashboard contains exploratory graphs by demographics, cancer stage, tumor information, and by geographic location. Please select a tab on the left navigation bar to explore the data by your area of interest.
                </p>

                </CardBody>

            </Card>
          </Col>
        </Row>


      </div>
    </>
  );
}

export default About;
